mapper.select:

SELECT 
	@rownum := @rownum + 1 as rn, 
	b.*
FROM (SELECT @rownum := #{startRowIndex} FROM dual) tmp, board b
ORDER BY board_id ASC
LIMIT #{startRowIndex}, #{rowsPerPage}

mapper.update:
update 
	board 
set 
	title = #{title}, content = #{content}, imagename = #{imagename}, date = #{date} 
where board_id = #{board_id}
